package POCORE;

public class StringSwitchds {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String festival="my brithday";
	    switch(festival)
	    {
	    case "onam": System.out.println("it is celebrated in september");
	               break;
	    case "vishu": System.out.println("it is celebrated in april");
	               break;
	    case "pongal": System.out.println("it is celebrated in january");
	               break;
	    case"Deepavali": System.out.println("it is celebrated in october");
	               break;
	    default:System.out.println("No celebrations");                                                                         
	    }

	}

}
