package String;

public class replace_swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String c  = new String(" the sky is bule colour");
		System.out.println(c.replace("sky", "ocean"));
		String e = c.replace("sky", "ocean");
		System.out.println(e.replace("bule", "darkbule"));
		
		
	}

}
